/* eslint-disable react/void-dom-elements-no-children */
/* eslint-disable react/no-unused-state */
import React, { Component } from 'react';

import { FaPlus } from 'react-icons/fa';

import { Container, Form, SubmitButton, List } from './styles';
import deezer from '../../assets/deezer.png';
import api from '../../services/api';

// eslint-disable-next-line react/prefer-stateless-function
export default class Main extends Component {
  // eslint-disable-next-line react/state-in-constructor
  state = {
    newMusc: '',
    artistas: [],
    loading: false,
  };

  componentDidMount() {
    const artistas = localStorage.getItem('artistas');

    if (artistas) {
      this.setState({ artistas: JSON.parse(artistas) });
    }
  }

  componentDidUpdate(_, prevState) {
    const { artistas } = this.state;
    if (prevState.artistas !== artistas) {
      localStorage.setItem('artistas', JSON.stringify(artistas));
    }
  }

  handleInput = e => {
    this.setState({ newMusc: e.target.value });
  };

  handleSubmit = async e => {
    e.preventDefault();

    this.setState({ loading: true });

    const { newMusc, artistas } = this.state;

    const response = await api.get(`${newMusc}`);

    const data = {
      nome: response.data.name,
      detalhes: response.data.link,
    };

    this.setState({
      artistas: [...artistas, data],
      newMusc: '',
      loading: false,
    });
  };

  render() {
    const { newMusc, artistas, loading } = this.state;
    return (
      <Container>
        <h1>
          <img src={deezer} alt="deezer" height={48} />
          Artistas
        </h1>

        <Form onSubmit={this.handleSubmit}>
          <input
            type="text"
            placeholder="Adicionar Artista"
            value={newMusc}
            onChange={this.handleInput}
          />

          <SubmitButton loading={loading}>
            <FaPlus color="#fff" size={14} />
          </SubmitButton>
        </Form>
        <List>
          {artistas.map(arts => (
            <li key={arts.nome}>
              <span>{arts.nome}</span>
              <link href={arts.detalhes}>Detalhes</link>
            </li>
          ))}
        </List>
      </Container>
    );
  }
}
