import styled from 'styled-components';

export const Container = styled.div`
  max-width: 700px;
  background: #fff;
  border-radius: 4px;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
  padding: 30px;
  margin: 80px auto;
  h1 {
    font-size: 20px;
    display: flex;
    align-items: center;
  }
  img {
    margin-right: 10px;
  }
`;

export const Form = styled.form`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
  input {
    flex: 1;
    border-radius: 4px;
    padding: 10px 15px;
    border: 1px solid #eee;
  }
`;

export const SubmitButton = styled.button.attrs(props => ({
  type: 'submit',
  disabled: props.loading,
}))`
  background: #111;
  border: 0;
  padding: 0 15px;
  margin-left: 10px;
  border-radius: 4px;

  display: flex;
  justify-content: center;
  align-items: center;

  &[disabled] {
    cursor: not-allowed;
    opacity: 0.6;
  }
`;

export const List = styled.ul`
  margin-top: 20px;
  list-style: none;
  display: flex;
  li {
    flex-direction: row;
    justify-content: space-between;
  }
`;
