/* eslint-disable react/no-unused-state */
import React, { Component } from 'react';

import { FaPlus } from 'react-icons/fa';

import { Container, Form, SubmitButton } from './styles';
import deezer from '../../assets/deezer.png';
import api from '../../services/api';

// eslint-disable-next-line react/prefer-stateless-function
export default class Main extends Component {
  // eslint-disable-next-line react/state-in-constructor
  state = {
    newMusc: '',
    artistas: [],
  };

  handleInput = e => {
    this.setState({ newMusc: e.target.value });
  };

  handleSubmit = async e => {
    e.preventDefault();

    this.setState({ loading: true });

    const { newMusc, artistas } = this.state;

    const response = await api.get(`${newMusc}`);

    const data = {
      nome: response.data.name,
    };

    this.setState({
      artistas: [...artistas, data],
      newMusc: '',
    });
  };

  render() {
    const { newMusc } = this.state;
    return (
      <Container>
        <h1>
          <img src={deezer} alt="deezer" height={48} />
          Artistas
        </h1>

        <Form onSubmit={this.handleSubmit}>
          <input
            type="text"
            placeholder="Adicionar Artista"
            value={newMusc}
            onChange={this.handleInput}
          />

          <SubmitButton>
            <FaPlus color="#fff" size={14} />
          </SubmitButton>
        </Form>
      </Container>
    );
  }
}
